import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { doApiGet, doApiMethod, URL_API } from '../../services/apiSer';

function ListProdAdmin(props) {
  let [prods_ar, setProdsAr] = useState([]);

  useEffect(() => {
    doApi();
  }, [])

  const doApi = async () => {
    let url = URL_API + "/prods?sort=_id&reverse=yes";
    let data = await doApiGet(url);
    setProdsAr(data);
  }

  const delProd = async(_id) => {
    if(window.confirm("are you sure you want to delete?")){
      let url = URL_API + "/prods/"+_id;
      let data = await doApiMethod(url,"DELETE",{});
      if(data.n == 1){
        //refresh the table
        doApi();
      }
      else{
        alert("there problem")
      }
    }
  }

  return (
    <div>
      <h1>List of Prod in shop:</h1>
      <Link to="/admin/addProd">add new prod</Link>
     

      <table className="table table-striped">
        {/* TODO: mouseover will shoe info of prod */}
        <thead>
          <tr>
            <th>#</th>
            <th>name</th>
            <th>category</th>
            <th>price</th>
            <th>qty</th>
            <th>user</th>
            <th>del/edit</th>
          </tr>
        </thead>
        {/* TODO: add pagenation */}
        <tbody>

          {prods_ar.map((item,i) => {
            return (
              <tr key={item._id}>
                <td>{i+1}</td>
                <td>{item.name}</td>
                <td>{item.category_s_id}</td>
                <td>{item.price}</td>
                <td>{item.qty}</td>
                <td>{item.user_id}</td>
                <td>
                  <button onClick={() => {
                    delProd(item._id);
                  }} className="btn btn-danger">del</button>
                  <Link to={"/admin/EditProd/"+ item._id} className="btn btn-dark ms-2">edit</Link>
                </td>
              </tr>
            )
          })}

        </tbody>
      </table>
    </div>
  )
}

export default ListProdAdmin;
